CREATE TABLE IF NOT EXISTS `ims_lionfish_community_head` ( `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '自增id', `uniacid` int(10) NOT NULL COMMENT '公众号id', `member_id` int(10) NOT NULL COMMENT '会员id', `agent_id` int(10) DEFAULT '0' COMMENT '推荐人id', `level_id` int(10) DEFAULT '0' COMMENT '团长等级id', `community_name` varchar(255) DEFAULT NULL COMMENT '小区名称', `head_name` varchar(100) NOT NULL COMMENT '团长名称', `head_mobile` varchar(50) NOT NULL COMMENT '团长手机号', `groupid` int(10) DEFAULT '0' COMMENT '分组id', `wechat` varchar(100) DEFAULT NULL COMMENT '微信号', `province_id` int(10) NOT NULL COMMENT '省', `city_id` int(10) NOT NULL COMMENT '市', `country_id` int(10) NOT NULL COMMENT '镇/街道', `area_id` int(10) NOT NULL COMMENT '区', `address` varchar(200) NOT NULL COMMENT '详细地址', `lon` float NOT NULL DEFAULT '0' COMMENT '经度', `lat` float NOT NULL DEFAULT '0' COMMENT '纬度', `state` tinyint(1) NOT NULL COMMENT '状态0 未审核，1已审核,2未审核', `enable` tinyint(1) DEFAULT '1' COMMENT '禁用状态 0：禁用 1：启用', `rest` tinyint(1) DEFAULT '0' COMMENT '团长休息', `is_default` tinyint(1) DEFAULT '0' COMMENT '0非默认，1 默认，只有开启单团长模式下可用', `apptime` int(10) NOT NULL COMMENT '申请时间', `is_modify_shipping_method` tinyint(1) DEFAULT '0' COMMENT '0跟随系统，1，开启，2关闭', `is_modify_shipping_fare` tinyint(1) DEFAULT '0' COMMENT '是否开启自定义运费', `shipping_fare` float(10,2) DEFAULT '0.00' COMMENT '配送费', `addtime` int(10) NOT NULL COMMENT '成为团长时间', PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`,`member_id`), KEY `lon` (`lon`,`lat`), KEY `agent_id` (`agent_id`) ) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='超级社区团购团长表';
CREATE TABLE IF NOT EXISTS `ims_lionfish_community_head_commiss` ( `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '自增ID', `uniacid` int(10) NOT NULL COMMENT '公众号id', `member_id` int(10) NOT NULL COMMENT '会员ID', `head_id` int(10) NOT NULL COMMENT '团长ID', `money` float(10,2) DEFAULT '0.00' COMMENT '可提现金额', `dongmoney` float(10,2) DEFAULT '0.00' COMMENT '提现中金额', `getmoney` float(10,2) DEFAULT '0.00' COMMENT '已提现金额', `bankname` varchar(200) DEFAULT NULL COMMENT '银行名称', `bankaccount` varchar(200) DEFAULT NULL COMMENT '卡号', `bankusername` varchar(200) DEFAULT NULL COMMENT '持卡人姓名', `share_avatar` varchar(200) DEFAULT NULL COMMENT '团长群分享头像', `share_wxcode` varchar(200) DEFAULT NULL COMMENT '团长群分享二维码', `share_title` varchar(200) DEFAULT NULL COMMENT '团长群分享标题', `share_desc` varchar(255) DEFAULT NULL COMMENT '团长群分享描述', PRIMARY KEY (`id`), KEY `member_id` (`member_id`) USING BTREE, KEY `uniacid` (`uniacid`), KEY `uniacid_2` (`uniacid`,`head_id`) ) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_lionfish_community_head_commiss_order` ( `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '自增ID', `uniacid` int(11) NOT NULL COMMENT '公众号id', `member_id` int(10) NOT NULL COMMENT '会员ID', `head_id` int(10) NOT NULL COMMENT '团长id', `child_head_id` int(10) DEFAULT '0' COMMENT '下级团长id', `type` enum('orderbuy','commiss','tuijian') NOT NULL DEFAULT 'orderbuy' COMMENT 'orderbuy 团员订单购买 ,commiss 下级的会员订单购买,tuijian 推荐团长的佣金', `fen_type` tinyint(1) DEFAULT '0' COMMENT '0,按照比例计算的，1按照实际金额计算的', `bili` float(10,2) DEFAULT '0.00' COMMENT '分销分佣比例', `level` int(10) DEFAULT '0' COMMENT '第几层分佣', `order_id` int(10) NOT NULL COMMENT '订单ID', `order_goods_id` int(10) DEFAULT '0' COMMENT '商品订单id', `state` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否结算：0，待计算。1，已结算。2，订单取消', `money` float(10,2) NOT NULL DEFAULT '0.00' COMMENT '分佣金额', `add_shipping_fare` float(10,2) DEFAULT '0.00' COMMENT '团长配送费', `addtime` int(10) NOT NULL COMMENT '添加时间', PRIMARY KEY (`id`), KEY `member_id` (`member_id`) USING BTREE, KEY `uniacid` (`uniacid`), KEY `uniacid_2` (`uniacid`,`head_id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
