ULT '0.00' COMMENT '满多少', `old_shipping_fare` decimal(10,2) DEFAULT '0.00' COMMENT '原来运费', `changedshipping_fare` decimal(10,2) DEFAULT '0.00' COMMENT '更改的运费', `shipping_no` varchar(255) DEFAULT '0' COMMENT '快递单号', `shipping_traces` text COMMENT '物流轨迹', `payment_code` varchar(128) NOT NULL COMMENT '支付方式', `score_for_money` float(10,2) DEFAULT '0.00' COMMENT '积分抵扣金额', `transaction_id` varchar(100) NOT NULL COMMENT '微信支付交易号', `comment` text NOT NULL, `remarksaler` varchar(255) DEFAULT NULL COMMENT '商家备注', `total` decimal(15,4) NOT NULL DEFAULT '0.0000', `old_price` decimal(10,2) DEFAULT '0.00' COMMENT '原价', `changedtotal` decimal(10,2) DEFAULT '0.00' COMMENT '更改的订单总价', `voucher_id` int(11) NOT NULL DEFAULT '0' COMMENT '优惠券id', `voucher_credit` float(10,2) NOT NULL DEFAULT '0.00' COMMENT '优惠券金额', `fullreduction_money` float(10,2) DEFAULT '0.00' COMMENT '满减金额', `order_status_id` int(11) NOT NULL, `last_refund_order_status_id` int(10) DEFAULT '0' COMMENT '申请退款时，该订单的上一订单状态', `is_balance` tinyint(1) NOT NULL DEFAULT '0' COMMENT '未结算', `lottery_win` tinyint(1) DEFAULT '0' COMMENT '0未中奖，1中奖', `ip` varchar(40) NOT NULL, `is_zhuli` tinyint(1) DEFAULT '0' COMMENT '是否砍价，0标识否，2标识砍价', `soli_id` int(10) DEFAULT NULL COMMENT '接龙id', `is_commission` tinyint(1) DEFAULT '0' COMMENT '是否分销订单', `is_delivery_flag` tinyint(1) DEFAULT '0' COMMENT '是否已经打包', `is_print_suc` tinyint(1) DEFAULT '1' COMMENT '是否打印成功。', `ip_region` varchar(20) NOT NULL, `user_agent` varchar(255) NOT NULL COMMENT '用户系统信息', `date_added` int(11) NOT NULL, `date_modified` int(11) NOT NULL, `pay_time` int(11) NOT NULL COMMENT '付款时间', `canceltime` int(10) DEFAULT NULL COMMENT '取消订单时间', `receive_time` int(10) DEFAULT NULL COMMENT '确认收货时间', `finishtime` int(10) DEFAULT NULL COMMENT '订单完成时间', `express_time` int(10) DEFAULT '0' COMMENT '发货时间', `express_tuanz_time` int(10) DEFAULT NULL COMMENT '送达团长时间', `shipping_cha_time` int(10) DEFAULT '0' COMMENT '查询时间', PRIMARY KEY (`order_id`), KEY `uniacid` (`uniacid`), KEY `member_id` (`member_id`), KEY `order_status_id` (`order_status_id`), KEY `date_added` (`date_added`), KEY `pay_time` (`pay_time`), KEY `head_id` (`head_id`), KEY `supply_id` (`supply_id`), KEY `order_num_alias` (`order_num_alias`), KEY `soli_id` (`soli_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
